    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        double A,B,C,X1,X2,D;
        A = Double.parseDouble(Par_A.getText());
        B = Double.parseDouble(Par_B.getText());
        C = Double.parseDouble(Par_C.getText());
        D = (Math.pow(B, 2) - 4*A*C);
        if (D >= 0) {

            if (A != 0 &&(B != 0 && C !=0)) {
                X1 = (-B + Math.sqrt(D))/(2*A);
                X2 = (-B - Math.sqrt(D))/(2*A);
                Sol_1.setText(String.valueOf(X1));
                Sol_2.setText(String.valueOf(X2));
            }
            if (B == 0 && (A != 0 && C !=0)) {
                X1 = Math.sqrt(-C/A);
                X2 = Math.sqrt(-C/A);
                Sol_1.setText(String.valueOf(X1));
                Sol_2.setText(String.valueOf(X2));
            }
            if (C==0 && (A != 0 && B != 0)) {
                X1 = 0;
                X2 = -B/A;
                Sol_1.setText(String.valueOf(X1));
                Sol_2.setText(String.valueOf(X2));
            }
            if (C==0 && B == 0) {
                X1 = 0;
                X2 = 0;
                Sol_1.setText(String.valueOf(X1));
                Sol_2.setText(String.valueOf(X2));
            }
            }else {
                Sol_1.setText(String.valueOf("No hay soluci√≥n"));

                Sol_2.setText(String.valueOf("No hay soluci√≥n"));
            }
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

